javascript.jsitem:
var params = arguments[0],
		$ = skuid.$,
		model = skuid.$M('pgs__pagedata'),
		conditions = model.conditions,
		filterModel = skuid.$M('pgs__FiltersApplied_UI'),
		booleanFilters = ['isMaster','createdByMe','updatedByMe','inMigration'],
		appliedFilters = [];

$.each(conditions, function(i, row) {
	if(booleanFilters.includes(row.name) && row.inactive==false) {
		appliedFilters.push({field: row.name + "Filter", value: true});
	}
	else if (row.name == 'apiVersion' && row.value && row.inactive==false) {
		appliedFilters.push({field: row.name + "Filter", value: row.value});
	}
});

if (appliedFilters.length > 0) {
	filterModel.createRow({
		additionalConditions: appliedFilters, doAppend: true
	});
	filterModel.save();
}

