(function(){
var params = arguments[0],
	$ = skuid.$,
	model = params.model;

var cache = {};
var fk = "";
var targetModel = "";
var propertyMap = {};

switch(model.id){
	case "DataSourcePermissions":
		fk = "data_source_id";
		targetModel = "DataSource";
		break;
	case "AppPermissions":
		fk = "app_id";
		targetModel = "App";
		propertyMap = {
			is_default: "default"
		};
		break;
	case "NamedPermissions":
		fk = "permission";
		targetModel = "Named";
		break;
	case "DataSourceObjectPermissions":
		fk = "data_source_object_id";
		targetModel = "DataSourceObject";
		propertyMap = {
			queryable: "queryable",
			createable: "createable",
			updateable: "updateable",
			deleteable: "deleteable",
		}
		break;
}

if(!fk) return;

var target = skuid.$M(targetModel);

$.each(model.data, function(index, data){
	var id = data[fk];
	cache[id] = {};
	$.each(propertyMap,function(db_key,object_key){
		cache[id][object_key] = data[db_key];
	});
});

var rowsToUpdate = {};
$.each(target.getRows(), function(index, row){
	rowsToUpdate[row.id] = {enabled: (typeof cache[row.id] === "object")};
	$.each(propertyMap,function(db_key,object_key){
		rowsToUpdate[row.id][object_key] = cache[row.id] && cache[row.id][object_key];
	});
});

target.updateRows(rowsToUpdate);
// This is a bit hacky, but it resets the model to an "unchanged state" without resetting our data.
target.hasChanged = false;
target.originals = $.extend({},target.changes);
target.changes = {};
target.cancel();

})(arguments[0]);

var params = arguments[0],
	$ = skuid.$;

var dataSourcePermissions = skuid.$M("DataSource");
var appPermissions = skuid.$M("App");
var namedPermissions = skuid.$M("Named");
var profile = skuid.$M("Profile").getFirstRow();

var state = {
	name: profile.name,
    appPermissions: {},
    dataSourcePermissions: {},
    namedPermissions: {}
};

$.each(dataSourcePermissions.getRows(), function(index, row){
	if (row.enabled) {
        state.dataSourcePermissions[row.name] = true;
	}
});

$.each(appPermissions.getRows(), function(index, row){
    if (row.enabled) {
        state.appPermissions[row.name] = {};
        state.appPermissions[row.name].isDefault = row.default ? row.default : false;
    }
});
if (profile.is_system) {
	state.namedPermissions = null;
} else {
    $.each(namedPermissions.getRows(), function(index, row){
		if (row.enabled) {
        	state.namedPermissions[row.id] = true;
		}
    });    
}

return $.ajax({
    url: "/api/v1/profile/edit",
    type: "PUT",
    data: JSON.stringify(state),
    contentType: "application/json"
});

var params = arguments[0],
	$ = skuid.$,
    model = params.model,
    row = params.row,
    rowsToUpdate = {},
    hasDefault = false;

	// Figure out if one of our apps is marked as default.
	$.each( model.getRows(), function(){
		if(this.id === row.id && !row.enabled) {
			rowsToUpdate[row.id] = { default: false };
		} else if (this.default === true) {
			hasDefault = true;
		}
	});

	// If we need to add a default, add it to the first one we encounter.
	if (!hasDefault) {
		$.each( model.getRows(), function(){
			if(this.enabled === true) {
				rowsToUpdate[this.id] = { default: true };
				return false;
			}
		});
	}

	model.updateRows( rowsToUpdate );

var field = arguments[0],
    value = arguments[1],
    model = field.model,
    fieldRow = field.row,
    $ = skuid.$;

    $('<input type="radio">')
      // For some reason the Skuid theme
      // does not apply the 20px width to anything but checkboxes
      .css("width", "20px")
      .prop("name", "fakeRadioButton")
      .prop("checked", value)
      .on("change", function() {
        var rowsToUpdate = {};
        model.getRows().forEach(function(row){
          rowsToUpdate[model.getRowId(row)] = { default: fieldRow === row };
        });
        model.updateRows( rowsToUpdate, { initiatorId: field._GUID } );
      })
      .appendTo(field.element);

