var params = arguments[0],
  $ = skuid.$,
  model = skuid.$M("Profile"),
  row = model.getFirstRow(),
  profileName = model.getFieldValue(row, "name", true),
  signupApiPath = model.getFieldValue(row, "signup_api_prefix", true);

$.blockUI({
    message: "Generating custom signup pages...",
    styleSettingsVariant: "dark"
});

return skuid.ajax.direct({
    url: "/api/v1/pages/create-custom-signup-pages?" + skuid.utils.param({
      "profileName": profileName,
      "signupApiPath": signupApiPath,
    }),
    method: "POST",
}).then(function(response) {
    var signupPageId = response.body.signupPageId;
    var confirmationPageId = response.body.confirmationPageId;
    var verifyPageId = response.body.verifyPageId;

    model.updateRow(row, {
        "signup_page_id": signupPageId,
        "signup_page": {
            "name": profileName + "_Signup",
            "id": signupPageId,
        },
        "signup_confirmation_page_id": confirmationPageId,
        "signup_confirmation_page": {
            "name": profileName + "_SignupConfirmation",
            "id": confirmationPageId,
        },
        "verify_page_id": verifyPageId,
        "verify_page": {
            "name": profileName + "_Verify",
            "id": verifyPageId,
        },
    });

    return model.save();
}).then(function() {
    $.unblockUI();
}).catch(function(err) {
  var message = skuid.utils.isString(err) ? err : err.message; 
  $.blockUI({
     message: "There was an error creating custom signup pages: " + message,
     styleSettingsVariant: "dark",
     timeout: 5000
  });
});

var model = skuid.$M("Profile"),
    row = model.getFirstRow(),
    SettingsModel = skuid.$M("RemoveSignupPagesUI"),
    profileName = model.getFieldValue(row, "name", true),
    DeletePages = SettingsModel.getFirstRow().DeletePages === true;

model.updateRow(row, {
   "signup_page_id": null,
   "signup_confirmation_page_id": null,
   "verify_page_id": null,
   "signup_ui": null,
});

return model.save().then(function() {
    if (DeletePages) {
        return skuid.ajax.direct({
            url: "/api/v1/pages/delete-custom-signup-pages?profileName=" + profileName,
            method: "POST",
        });
    } else {
        return skuid.utils.resolve();
    }
}).catch(function(error) {
    $.blockUI({
        message: "There was an error removing custom signup pages for this Profile: " + error.message,
        styleSettingsVariant: "dark",
    });
});

