(function(skuid){
	var $ = skuid.$;
	$(document.body).one('pageload',function(){

		// Only query the Native App model(s) if this site has the "Native" feature flag enabled.
		// Otherwise, the user will get page errors.
		var nativeAppModel = skuid.model.getModel('NativeApp'),
		    hasNativeAppPackagesModel = skuid.model.getModel('hasNativeAppPackages'),
		    hasNativeFeatureFlag = skuid.platform.hasFeature('Native');

	    if (hasNativeFeatureFlag) {
	        // Note: We are loading these models separately to avoid an issue with the scenario where no data is present in the NativeApp model.
			// If we attempt to load the models at the same time, and no data is present in the NativeApp model, then we get an error on the hasNativeAppPackages model related to an empty value for the {{id}} URL query parameter.
			skuid.model.load([nativeAppModel]).then(function () {
				if (nativeAppModel.data.length > 0) {
					skuid.model.load([hasNativeAppPackagesModel])
						.then(function () {
							// Re-render the NativePackagesList List component. This is necessary because of an issue with the Package(s) column merge syntax, where global merge syntax is not updated when a model (e.g. the hasNativeAppPackages model) is updated.
							// See DSC-412.
							// TODO: Remove this after DSC-412 is fixed.
							skuid.component.getById('NativePackagesList').render();
						});
				}
			});
	    }
	});
})(skuid);

