// A snippet to update the Routes's base_url field based on the new page name 

var params = arguments[0],
    model = params.model,
    row = params.row,
    updatedRouteName,
    resourceName,
    matches,
	$ = skuid.$;

if (!model || !row) { return; }

updatedRouteName = model.getFieldValue(row, 'name');

// Look for matches of accepted characters
matches = updatedRouteName.match(/[a-z]|[A-Z]|[0-9]|\s/g);
if (matches && matches.length) {
    // If matches are found, join these together in a string, and replace all spaces with hyphens
    resourceName = matches.join('').replace(/\s/g,"-");
} else {
    // If no matches are found, just use an empty string
    resourceName = '';
}

model.updateRow(row, {'temp_new_Route_Name':resourceName});

const params = arguments[0],
$ = skuid.$,
key = params.$Input.key,
sourceRows = skuid.$M(params.$Input.sourceModel).getRows(),
modelForAdding = skuid.$M(params.$Input.modelForAdding),
condName = params.$Input.conditionName;

// create list of items to exclude
var filterList = "";
sourceRows.forEach(function (item, index) {
    filterList = filterList+='"'+item[key]+'",';
})
filterList = filterList.slice(0, filterList.length - 1);

// add list to condition on model and requery
modelForAdding.setCondition(modelForAdding.getConditionByName(condName), filterList);
return modelForAdding.updateData();

const params = arguments[0],
    $ = skuid.$,
    inputs = params.$Input,
    baseId = inputs["junctionBaseFieldValue"],
    rowsToAdd = skuid.$M(inputs["modelWithRowsToAdd"]).getRows(),
    baseField = inputs["junctionBaseFieldName"],
    fieldTwo = inputs["junctionFieldTwoName"],
    junctionModel = skuid.$M(inputs["junctionModel"]);    

// empty rows from junction model
junctionModel.abandonAllRows();

// filter items to only those that have been selected
let filteredItems = rowsToAdd.filter(function(e) {
    return e.selected;
});

// loop through selected items to create junction row
$.each(filteredItems, function (i, row) {
    junctionModel.createRow({
        additionalConditions: [
            { field: baseField, value: baseId },
            { field: fieldTwo, value: row["id"] }
        ], doAppend: true
    });
});

const params = arguments[0],
$ = skuid.$;

let userModel =  skuid.$M("UsersForApp"),
    userArray = userModel.getRows(),
    userpermSetArray = skuid.$M("UserPermSets").getRows(),
    group = [];

userpermSetArray.map((value) => {
    let user_id = value.user_id;

    if (!group[user_id]) {
        group[user_id] = [];
    }
    // add permission set name to the front of the array
    group[user_id].unshift(value.permissionSet.name);
});

// loop through each user record and populate the otherPermSetNames
// field with the corresponding group of perm sets
$.each(userArray, function(i,row){
    var permSetRow = group[row.user_id];
    if (permSetRow) {
        // add first perm set name to model, removing it from the array in the process
        var firstPermSet = permSetRow.pop();
            userModel.updateRow(row, {
                firstPermSetName: firstPermSet
            });
        // if there are other names add them as well
        if (permSetRow.length > 0) {
            var formattedPermSets = permSetRow.toString().replace(/,/g, '<br>');
            userModel.updateRow(row, {
                otherPermSetNames: formattedPermSets 
            });
        }

    }

});

const params = arguments[0],
    $ = skuid.$,
    userModel = skuid.$M("UsersToAdd"),
    users = userModel.getRows(),
    permSetModel = skuid.$M("PermissionSets");
    permSets = permSetModel.getRows(),
    userPermModel = skuid.$M("UserPermSets"),

// empty rows from user perm sets model
userPermModel.abandonAllRows();

// filter users and perm sets to only those that have been selected
let filteredUsers = users.filter(function(e) {
    return e.selected;
});
let filteredPermSets = permSets.filter(function(e) {
    return e.selected;
});

// loop through selected users and perm sets to create junction row in user perm sets
$.each(filteredUsers, function (i, userRow) {
    $.each(filteredPermSets, function (i, permSetRow) {
        userPermModel.createRow({
            additionalConditions: [
                { field: 'user_id', value: userRow.id },
                { field: 'permission_set_id', value: permSetRow.id }
            ], doAppend: true
        });
    });
});

