const params = arguments[0],
    $ = skuid.$,
    inputs = params.$Input,
    routeBuilderModel = skuid.$M(inputs["route_builder"]);
    routeBuilderRows = routeBuilderModel.getRows(),
    routeModel = skuid.$M(inputs["route_model"]),
    routeId = inputs["route_id"];
    helperModel = skuid.$M(inputs["helper_model"]),
    helperRow = helperModel.getFirstRow();

    //if there isn't a route row found by id pull the first row (new rows can't be found by id
    //and are always the first row in the model)
    var routeRow = routeModel.getRowById(routeId) ? routeModel.getRowById(routeId) : routeModel.getFirstRow();
    if (routeRow == "") {
        return;
    }

    var route = "",
    show_opt_param = false,
    has_invalid_char = false;
    //loop through model with route segments, constructing the route string
    $.each(routeBuilderRows,function(i,row){
        var type = row.type, // Text or Parameter
        value = (row.value) ? row.value : "",
        delimiter = (i == 0) ? "" : "/", // the beginning of the route shouldn't have a /
        not_alphanum = /[^a-zA-Z0-9_-]/;

        // if one of the rows is blank or has an invalid character
        if (value == "" | not_alphanum.test(value)) {
            has_invalid_char = true;
        }

        if (type == "Text") {
            route = route + delimiter + value;
        } else if (type == "Parameter") {
            // if the segment is the last one in the route show the optional parameter option
            if (i+1 == routeBuilderRows.length) {
                show_opt_param = true;
                //update row builder row
                routeBuilderModel.updateRow(row, {
                    show_is_optional: true
                });
            } 
            // if the builder row is marked as optional and the segment is the last one
            if (row.optional && (i+1 == routeBuilderRows.length)) {
                route = route + delimiter + "{" + value + "?}";
            } else {
                route = route + delimiter + "{" + value + "}";
            }
        }
    });

    routeModel.updateRow(routeRow, {
        route: route,
        ui_has_invalid_character: has_invalid_char
    });

const params = arguments[0],
    $ = skuid.$,
    inputs = params.$Input,
    segmentModel = skuid.$M(inputs["model_to_put_segments"]),
    helperModel = skuid.$M(inputs["helper_model"]),
    helperRow = helperModel.getFirstRow(),
    routeId = inputs["route_id"],
    routeModel = skuid.$M(inputs["model_with_route"])
    routeRow = routeModel.getRowById(routeId),
    route = routeRow ? routeRow.route : "",
    segments = route.split("/");

    // go through the route, spliting it into segments
    var is_optional = false;
    $.each(segments,function(i,item) {
        var value = item,
        type = "Text",
        hasParams = value.match(/{(.*)}/);

        if (hasParams && hasParams.length == 2) {
            value = hasParams[1],
            type = "Parameter";
            if (value.endsWith("?")) {
                value = value.substring(0,value.length-1);
                is_optional = true;
            }
        }
        segmentModel.createRow({
            additionalConditions: [
                { field: 'type', value: type },
                { field: 'value', value: value },
                { field: 'route_id', value: routeId },
                { field: 'optional', value: is_optional },
                { field: 'show_is_optional', value: is_optional }
            ], doAppend: true
        });
    });
    // do_update makes sure the is_optional value doesn't get cleared by other actions
	helperModel.updateRow(helperRow, {
        do_update: false
    });

const params = arguments[0],
    $ = skuid.$,
    route = params.$Input.URL,
    paramsModel = skuid.$M("apps__RouteParameters");

paramsModel.emptyData();
paramsModel.adoptRows(
    skuid.lib.Mustache.parse(route, ["{", "}"])
        .filter(part => part[0] === "name")
        .map(part => {
            let parameterName = part[1],
                hasWildcard = parameterName.indexOf("*") > -1,
                hasOptionalMarker = parameterName.indexOf("?") > -1,
                isOptional = hasOptionalMarker || hasWildcard;

            if (hasOptionalMarker) parameterName = parameterName.replace("?", "");
            if (hasWildcard) parameterName = parameterName.substring(0, parameterName.indexOf("*"));

            return {
                IsOptional: isOptional,
                Name: parameterName,
                FullName: "{" + part[1] + "}",
            };
        })
);

