// A snippet to update the App's base_url field based on an updated App name

var params = arguments[0],
    model = params.model,
    row = params.row,
    updatedAppName,
    resourceName,
    matches,
	$ = skuid.$;

if (!model || !row) { return; }

updatedAppName = model.getFieldValue(row, 'name');

// Look for matches of accepted characters
matches = updatedAppName.match(/[a-z]|[A-Z]|[0-9]|\s/g);
if (matches && matches.length) {
    // If matches are found, join these together in a string, and replace all spaces with hyphens
    resourceName = matches.join('').replace(/\s/g,"-");
} else {
    // If no matches are found, just use an empty string
    resourceName = '';
}

model.updateRow(row, {'base_url': '/' + resourceName});

var params = arguments[0],
    $ = skuid.$;

var model = skuid.$M("FeatureFlags")
model.updateRow(model.getFirstRow(),{
    appPermSetsEnabled: skuid.platform.hasFeature("Permission Sets"), 
    enableAppCentered: skuid.platform.hasFeature("AppCentricDev"),
    enableV1: skuid.platform.hasFeature("EnableV1"),
    nativeEnabled: skuid.platform.hasFeature("Native")
});
