javascript.jsitem:
const params = arguments[0],
$ = skuid.$;

let permSetId = skuid.$M("Inc_PermissionSet").getFirstRow().id,
    objectId = skuid.$M("Inc_Datasource_Object").getFirstRow().id,
    pageName = "DSODetails_PageInclude",
    pageRef = skuid.runtime.getPage(pageName),
    showError = pageRef.actionSequences.getByName('Show error message');

function saveModel(model) {
    model.save({callback: function(result){
        if (!result.totalsuccess) {
            showError.run();
        }
    }});
}

// Create object permissions
let objectPermModel = skuid.$M("Inc_DataSourceObjectPermissions"),
    objectPermRows = objectPermModel.getRows();

if (objectPermRows.length == 0) {
    objectPermModel.createRow({
        additionalConditions: [
            { field: 'permission_set_id', value: permSetId },
            { field: 'data_source_object_id', value: objectId},
            { field: 'createable', value: false },
            { field: 'updateable', value: false },
            { field: 'queryable', value: false },
            { field: 'deleteable', value: false },
        ], doAppend: true
    });

    saveModel(objectPermModel);
}

// Create field permissions
let permModel =  skuid.$M("Inc_DataSourceFieldPermissions"),
    permRows = permModel.getRows(),
    permLength = permRows.length,
    itemModel = skuid.$M("Inc_Datasource_Fields"),
    itemRows = itemModel.getRows(),
    itemLength = itemRows.length,
    allPerms = [];

// if there are fields
if (itemLength > 0) {
    if (permLength == 0) {
        $.each(itemRows, function(i,row) {
            permModel.createRow({
                additionalConditions: [
                    { field: 'permission_set_id', value: permSetId },
                    { field: 'data_source_field_id', value: row.id },
                    { field: 'createable', value: false },
                    { field: 'updateable', value: false },
                    { field: 'queryable', value: false },
                ], doAppend: true
            });
        });
    } else if (permLength < itemLength) {
        $.each(permRows, function (i, row){
            allPerms.push(row.data_source_field_id);
        });

        $.each(itemRows, function(i, row){
            if (!allPerms.includes(row.id)) {
                permModel.createRow({
                    additionalConditions: [
                        { field: 'permission_set_id', value: permSetId },
                        { field: 'data_source_field_id', value: row.id },
                        { field: 'createable', value: false },
                        { field: 'updateable', value: false },
                        { field: 'queryable', value: false },
                    ], doAppend: true
                });
            }
        });
    }

    saveModel(permModel);
}


// Create condition permissions
let condPermModel =  skuid.$M("Inc_DataSourceConditionPermissions"),
    condPermRows = condPermModel.getRows(),
    condPermLength = condPermRows.length,
    condModel = skuid.$M("Inc_Datasource_Conditions"),
    condRows = condModel.getRows(),
    condLength = condRows.length,
    allCondPerms = [];

// if there are conditions
if (condLength > 0) {
    if (condPermLength == 0) {
        $.each(condRows, function(i,row) {
            condPermModel.createRow({
                additionalConditions: [
                    { field: 'permission_set_id', value: permSetId },
                    { field: 'data_source_condition_id', value: row.id },
                    { field: 'alwaysOn', value: false },
                ], doAppend: true
            });
        });
    } else if (condPermLength < condLength) {
        $.each(condPermRows, function (i, row){
            allCondPerms.push(row.data_source_condition_id);
        });

        $.each(condRows, function(i, row){
            if (!allCondPerms.includes(row.id)) {
                condPermModel.createRow({
                    additionalConditions: [
                        { field: 'permission_set_id', value: permSetId },
                        { field: 'data_source_condition_id', value: row.id },
                        { field: 'always_on', value: false },
                    ], doAppend: true
                });
            }
        });
    }

    saveModel(condPermModel);
}
