javascript.jsitem:
// JS specific to the NativeAppCreate page
(function(skuid){
	var $ = skuid.$,
	    STEP_TWO = 'step2',
	    WIZARD_ID = 'NativeAppCreateWizard';

	$(document.body).one('pageload',function(){
		var imageStatusModel = skuid.model.getModel('imageStatuses'),
		    imageStatusRow = imageStatusModel.getFirstRow(),
		    pageControlsModel = skuid.model.getModel('PageControls'),
		    pageControlsRow = pageControlsModel.getFirstRow(),
		    wizard,
		    navigateFunction;

		// If we have all of our images (icons, splashscreens) uploaded when the user visits the page, then navigate to step 2 automatically.
		if (imageStatusRow
		    && imageStatusModel.getFieldValue(imageStatusRow, 'hasIcon') === true
		    && imageStatusModel.getFieldValue(imageStatusRow, 'hasSplashScreens') === true) {

            // Look for our wizard component. If found, go ahead and navigate to step 2.
		    wizard = skuid.component.getById(WIZARD_ID);
		    wizard && (navigateFunction = wizard.getCPI().navigate);
		    /*if (navigateFunction) {
		        pageControlsModel && pageControlsRow && pageControlsModel.updateRow(pageControlsRow, {'WizardCurrentStepId': STEP_TWO});
		        navigateFunction(STEP_TWO);
		    }*/
		    navigateFunction && navigateFunction(STEP_TWO);

		    // Update our PageControls model WizardCurrentStepId value for the sake of conditional rendering of buttons
		    pageControlsModel && pageControlsRow && pageControlsModel.updateRow(pageControlsRow, {'WizardCurrentStepId': STEP_TWO});
		}
	});
})(skuid);
