const params = arguments[0],
    $ = skuid.$,
    permSetId = skuid.$M("PermissionSet").getFirstRow().id,
    dsRows = skuid.$M("AppDatasources").getRows(),
    dsPermissions = skuid.$M("DatasourcePermissions"),
    dsPermRows = dsPermissions.getRows(),
    allExistingPerms = [],
    pageName = "PermissionSetDetail",
    pageRef = skuid.runtime.getPage(pageName),
    showError = pageRef.actionSequences.getByName('Show error message');


// create array that stores all data source permission rows
$.each(dsPermRows, function(i, row) {
    allExistingPerms.push(row.data_source_id);
});

// go through all data source rows
$.each(dsRows, function (i, row){
    if (row.enabled) {
        // if the data source is enabled and there's not a permission record for it
        if (!allExistingPerms.includes(row.data_source_id)) {
            dsPermissions.createRow({
                additionalConditions: [
                    { field: 'data_source_id', value: row.data_source_id },
                    { field: 'permission_set_id', value: permSetId }
                ], doAppend: true
            });
        }

    } else {
        // if the datasource isn't enabled and there's a permissions record for it
        if (allExistingPerms.includes(row.data_source_id)) {
            let dsPermRowToDelete = dsPermRows.filter(function(e) {
                return e.data_source_id == row.data_source_id;
            });
            dsPermissions.deleteRow(dsPermRowToDelete);
        }
    }
});

// save data source permission model changes
return dsPermissions.save({callback: function(result){
    if (!result.totalsuccess) {
        showError.run();
    }
}});

