const params = arguments[0],
    $ = skuid.$,
    dsPermissions = skuid.$M("DatasourcePermissions"),
    dsPermRows = dsPermissions.getRows(),
    configModel = skuid.$M("ui_RenderControl"),
    configValue = configModel.getFirstRow().holdConfiguredDS;

if (configValue != "") {
    configArray = configValue.split(",");
    $.each(dsPermRows, function(i, row) {
        if (configArray.includes(row.id)) {
            dsPermissions.updateRow(row, {
                configured: true
            })
        }
    });
}

const params = arguments[0],
    $ = skuid.$,
    dsPermRows = skuid.$M("DatasourcePermissions").getRows(),
    dsModel = skuid.$M("AppDatasources"),
    dsRows = dsModel.getRows();

let configuredStr = "",
    configArray = [];

$.each(dsPermRows, function(i, row) {
    configuredStr = configuredStr+","+row.data_source_id;
});

if (configuredStr != "") {
    configArray = configuredStr.split(",");
}
$.each(dsRows, function(i, row) {
    if (configArray.includes(row.data_source_id)) {
        dsModel.updateRow(row, {
            enabled: true
        });
    } else {
        dsModel.updateRow(row, {
            enabled: false
        });
    }
});

const params = arguments[0],
    $ = skuid.$,
    key = params.$Input.key,
    modelWithAdditionsName = params.$Input.modelWithAdditions,
    modelWithAdditions = skuid.$M(modelWithAdditionsName), 
    modelRows = modelWithAdditions.getRows(),
    alreadyAssociatedRowsName = params.$Input.modelWithExistingRows,
    alreadyAssociatedRows = skuid.$M(alreadyAssociatedRowsName).getRows(),
    rowsToRemove = [],
    remove = [];

// put already included records into an array
$.each(alreadyAssociatedRows, function (i, row){
    rowsToRemove.push(row[key]);
});

// loop through all records, removing records that are already included
$.each(modelRows, function (i, row){
    if (rowsToRemove.includes(row.id)) {
        remove.push(row);
    }
});

// remove already included records from model
modelWithAdditions.abandonRows(remove);

const params = arguments[0],
    $ = skuid.$,
    permSet = skuid.$M("PermissionSet").getFirstRow(),
    userModel = skuid.$M("UsersToAdd");
    users = userModel.getRows(),
    userPermModel = skuid.$M("UserPermSets");

// empty rows from user perm sets model
userPermModel.abandonAllRows();

// filter users and perm sets to only those that have been selected
let filteredUsers = users.filter(function(e) {
    return e.selected;
});

// loop through selected perm sets to create junction row in user perm sets
$.each(filteredUsers, function (i, userRow) {
    userPermModel.createRow({
        additionalConditions: [
            { field: 'permission_set_id', value: permSet.id },
            { field: 'user_id', value: userRow.id }
        ], doAppend: true
    });
});
