var field = arguments[0],
    value = skuid.utils.decodeHTML(arguments[1]),
    metadata = field.metadata,
    originalEntries = metadata.picklistEntries;

// Run the main renderer, with "Always use default value" removed
metadata.picklistEntries = originalEntries.filter(function(entry) {
    return entry.value !== "default";
});

skuid.ui.fieldRenderers[metadata.displaytype][field.mode](field, value);
// Restore the original entries
metadata.picklistEntries = originalEntries;

var FieldRenderer = skuid.component.FieldRenderer;
var rows = {};
var	supportedFormulaNames = [
		"ABS",
		"CASE",
		"CEIL",
		"CONTAINS",
		"ENDS_WITH",
		"FLOOR",
		"IF",
		"ISBLANK",
		"JOIN_TEXT",
		"LEFT",
		"LEN",
		"LOWER",
		"NOT",
		"RIGHT",
		"ROUND",
		"STARTS_WITH",
		"UPPER",
		"VALUE",
	];
var $ = skuid.$;

return new FieldRenderer({
	// Options are "virtual", or "element",
	editModeStrategy: "element",
	edit: function (fieldComponent, starterDefinition) {
		// contains model, row, value, element, and field
		var context = fieldComponent.cpi.getContext();
		var element = context.element;

	    // We've already added the JSON editor, but it's been removed for some reason.
	    if (element.children.length === 0) {
	    	delete rows[context.row.id];
	    }

	    if (!rows[context.row.id]) {
	        rows[context.row.id] = true;
	        $(element).append(skuid.ui.renderers.FORMULA.edit({
                customAddMergeSelectorLabel: "Attribute",
                customAddMergeSelectorOptions: skuid.$M("stng__RecentSAMLAttributes").getRows().map(function(row){
                	return {
                		label: row.attribute,
                		value: row.attribute,
                	};
                }),
                formulaOptions: skuid.builder.core.formula.getFormulasAsItems().filter(function(item) {
                	return !item.formula.getNamespace() && supportedFormulaNames.includes(item.formula.getLocalName());
                }),
                onChange: function(newValue) {
                	context.model.updateRow(context.row,context.field.id,newValue,{ initiatorId: fieldComponent.id });
                },
                showCustomAddMergeSelector: true,
                showLabel: false,
                value: context.value,
            }));
	    }
	}
});

(function(skuid){
var $ = skuid.$;
	$(document.body).one('pageload',function(){
		addCSS('/assets/themes/next/builderlight/skuidtheme.css');

        // Include CSS file
        function addCSS(filename){
         var head = document.getElementsByTagName('head')[0];

         var style = document.createElement('link');
         style.href = filename;
         style.type = 'text/css';
         style.rel = 'stylesheet';
         head.append(style);
        }
	});
})(skuid);

var summaries = skuid.$M("stng__SSOConfig").getFirstRow().idp_certificate_summaries;
var expiryIndicator = "EXPIRATION: ";
if (summaries && summaries.length) {
    skuid.$M("IDPCertificateSummaries__ui").adoptRows(summaries.map(function(summary, index) {
		var expiryStartIndex = summary.indexOf(expiryIndicator);
		var expiryString = summary.substring(expiryStartIndex + expiryIndicator.length);
        var expiryDate = Date.parse(expiryString);
        return {
            index: index,
            summary: summary.substring(0, expiryStartIndex),
			expiration_date: expiryDate,
            is_expired: expiryDate < new Date(),
        };
    }).sort(function(a, b) {
		return b.expiration_date - a.expiration_date;
	})); 
}

