const params = arguments[0],
$ = skuid.$;

// model that will hold component names
var model = skuid.$M("ui__Components");

// get component pack resource name without file extension
var resourceName = params.row.resource_name;
var packName = params.row.name;

// example of endpoint we need to retrieve skuid_runtime.json file
// /static/componentpacks/1596055160045/newpackfooComponents/skuid_runtime.json

var today = new Date();
var now = today.getTime();

function alreadyExists(packName, componentName) {
    var modelRows = model.getRows();
    var flag = false;
    $.each(modelRows,function(i,row) {
        if (row.packName == packName && row.componentName == componentName) {
            flag = true;
        } 
    });
    return flag;
}

$.ajax({
	url: "/static/componentpacks/"+now+"/"+resourceName+"/skuid_runtime.json",
	type: "GET",
}).then(function(result) {
    var components = result.components;
    var apiVersion = result.skuidApiVersion;

    $.each(components,function(i,row){
        var componentName = row.id;
        if (apiVersion == "v2") {
            componentName = row.label;
        }
        // create row for each component that includes the pack name
        if (!alreadyExists(packName,componentName)) {
            model.createRow({
                additionalConditions: [
                    { field: 'packName', value: packName },
                    { field: 'componentName', value: componentName}
                ], doAppend: true
            });
        }
    });
}).catch(function(error) {
    var namedInputs = {
        "error" : error.statusText,
    };
    // pass error and context into action sequence
    skuid.actionSequences.getByName("Handle error").run(namedInputs,params);
});
