            }
          }
        }
      ]
    }, undefined, 2);
    var authModel = skuid.model.getModel('stng__AuthProvider_Detail');
    authModel.updateRow(authModel.getFirstRow(), {TrustRelationshipPolicy__ui: policyJSON});
    return h("div", {}, [
      h("pre", {styles:{margin:0}}, [policyJSON])
    ]);
  }
});

