const ESCKEY = "env_specific_config_map";
function escMerge(obj) {
	if (!obj) {
		return;
	}
	let configMap = {
		"database_url_config": "url",
		"database_username_config": "database_username",
		"database_password_config": "database_password",
		"database_name_config": "database_name",
	};

	for (let conf in configMap) {
		if (obj[conf]) {
			obj[ESCKEY] = {};
			break;
		}
	}

	for (let conf in configMap) {
		if (obj[conf]) {
			let id = obj[conf];
			obj[ESCKEY][configMap[conf]] = {
				"id": id,
			};
		}
	}
}

let params = arguments[0],
	changes = params.changes,
	model = params.model.data[0];
if (skuid.platform.hasFeature("Environment Specific Config")) {
    escMerge(model);
    // Always add variable settings to changes if they exist on model,
    // Otherwise they won't get saved because the fields are ui-only.
    if (model[ESCKEY]) {
        changes[ESCKEY] = model[ESCKEY];
    }
}
return changes;

