update.formula:
JOIN_TEXT(
    "",
    UPPER(LEFT({{name}},1)),
    IF (
        ENDS_WITH({{name}}, "y"),
        ( 
            LEFT(
                RIGHT(
                    {{name}}, ( LEN({{name}}) - 1 )
                ),
                ( LEN({{name}}) - 2 )
            )+"ies"
        ),
        RIGHT(
            {{name}}, ( LEN({{name}}) - 1 )
         ) +"s"
    )
)

javascript.jsitem:
var $ = skuid.$;
var siteEnvModel = skuid.$M("stng__SiteEnv");
var displayModel = skuid.$M("stng__SiteEnv_Outbound_IPs_uionly");
var siteEnvRow = siteEnvModel.getFirstRow();
var outboundIPs = siteEnvRow && siteEnvModel.getFieldValue(siteEnvRow,"PLINY_OUTBOUND_IP_ADDRESSES");

$.each(outboundIPs, function(index, ip) {
    displayModel.createRow({
        additionalConditions: [
            { field: 'outbound_ip', value: ip }    
        ]
    });
});

const ESCKEY = "env_specific_config_map";
function escMerge(obj) {
	var configFields = skuid.$M("ui__DataSourceConfigFields").data[0];
	if (!obj) {
		return;
	}
	let configMap = {
		"database_username_config": "database_username",
		"database_password_config": "database_password",
		"database_name_config": "database_name",
	};

	for (let conf in configMap) {
		if (configFields[conf]) {
			obj[ESCKEY] = {};
			break;
		}
	}

	for (let conf in configMap) {
		if (configFields[conf]) {
			let id = configFields[conf];
			obj[ESCKEY][configMap[conf]] = {
				"id": id,
			};
		}
	}
}

let params = arguments[0],
	changes = params.changes,
	model = params.model.data[0];
if (skuid.platform.hasFeature("Environment Specific Config")) {
    escMerge(model);

    // Always add variable settings to changes if they exist on model,
    // Otherwise they won't get saved because the fields are ui-only.
    if (model[ESCKEY]) {
        changes[ESCKEY] = model[ESCKEY];
    }
}
return changes;

var params = arguments[0],
	$ = skuid.$;
var model = skuid.$M("FeatureESC")
model.updateRow(model.getFirstRow(),{
    enabled: skuid.platform.hasFeature("Environment Specific Config"),
});

var params = arguments[0],
	$ = skuid.$;
const dataSourcesModel = skuid.$M("stng__DataSources");
let dataServiceID = dataSourcesModel.data[0].data_service_id;
let dataServiceName = "cloudWarden";
if (dataSourcesModel.data[0].data_service && dataSourcesModel.data[0].data_service.name) {
	dataServiceName = dataSourcesModel.data[0].data_service.name;
}
dataSourcesModel.updateRow(dataSourcesModel.getFirstRow(), {
    data_service_name: dataServiceName,
});
if (dataServiceID) {
  dataSourcesModel.updateRow(dataSourcesModel.getFirstRow(), {
    data_service_id: dataServiceID,
  });
}

var params = arguments[0],
	$ = skuid.$;
var dataSourceModel = skuid.$M("stng__DataSource_Warden")
dataSourceModel.updateRow(dataSourceModel.getFirstRow(), {
    "env_specific_config_map": {
        "database_name": "",
        "database_username": "",
        "database_password": "",
    },
});

